const h=new Intl.DurationFormat("en",{fractionalDigits:0,hoursDisplay:"auto",minutes:"numeric",minutesDisplay:"always",seconds:"2-digit",secondsDisplay:"always"}),u=new Intl.DateTimeFormat("en",{dateStyle:"medium",timeStyle:"short"}),D=new Intl.RelativeTimeFormat("en",{numeric:"auto",style:"short"}),M=a=>h.format({milliseconds:a%6e4,minutes:Math.floor(a/6e4)}),y=a=>u.format(a),d=(a,f=new Date)=>{const t=(l,c)=>D.format(l,c).split(/\./)[0],e=Math.floor((a.getTime()-f.getTime())/1e3);if(Math.abs(e)<60)return t(e,"second");const r=Math.floor(e/60);if(Math.abs(r)<60)return t(r,"minute");const n=Math.floor(r/60);if(Math.abs(n)<24)return t(n,"hour");const o=Math.floor(n/24);if(Math.abs(o)<7)return t(o,"day");const s=Math.floor(o/7);if(Math.abs(s)<4)return t(s,"week");const i=Math.floor(o/30);if(Math.abs(i)<12)return t(i,"month");const m=Math.floor(o/365);return t(m,"year")};export{d as a,y as b,M as f};
