def refresh_access_token(api_client, token_id=None):
    """Refresh the provided token id or fetch id for current token and refresh it

    Args:
        :param VNS3Client api_client: (required)
        token_id (int, optional): ID for token.

    Returns:
        :return: APIResponse
    """
    if not token_id:
        current_token = api_client.configuration.api_token
        tokens = get_access_tokens(api_client).response
        token_resource = None
        for token in tokens:
            if token["token"] == current_token:
                token_resource = token

        if not token_resource:
            raise ApiValueError(
                "Unexpected error: cant retrieve token id for current token"
            )
        token_id = token_resource["id"]
    return put_update_access_token(api_client, token_id, refresh=True)


