    def _get_current_branch(self) -> str:
        """Get current branch name."""
        try:
            import subprocess
            cmd = ['git', '-C', self.repo_path, 'branch', '--show-current']
            result = subprocess.run(cmd, capture_output=True, text=True, check=True)
            return result.stdout.strip()
        except:
            return "unknown"