import re

abb_patterns = re.compile(
    r"(?<!\w)(INC|LLC|LLP|LLLP|LP|LTD|CORP|CO|PLC|GMBH|AG|"
    r"SA|BV|SRL|SARL|PTY|ACC|ACCT|ACCTS|INST|INSTN|INSTNS|INSTL|GRP|GRPS|"
    r"INC\.|LTD\.|CO\.|CORP\.|LLP|PLLC|PC|ASSN|NATL|INTL|GR|"
    r"TRU|TR|TRUS|TRS|ADV)(?=\b|\s|$|[.,;:])",
    flags=re.IGNORECASE,
)

